tle a {";    echo "font-size:" . $data['single_title']['size'] . ";";    echo "line-height:" . $data['single_title']['height'] . ";";    echo "font-family:" . $data['general_font']['face'] . "}";}if ($data['sidebar_title']) {    echo "\n.widget .widget-title {";    echo "font-size:" . $data['sidebar_title']['size'] . ";";    echo "line-height:" . $data['sidebar_title']['height'] . ";";    echo "font-family:" . $data['general_font']['face'] . "}";}if ($data['footer_text']) {    echo "\n.sub-footer .copyright {";    echo "font-size:" . $data['footer_text']['size'] . ";";    echo "line-height:" . $data['footer_text']['height'] . "}";}if ($data['header_bg_color']) {    echo "\n#header .header-inner {";    echo "background-color:" . $data['header_bg_color'] . ";";    echo "border-bottom-color:" . adjustBrightness($data['header_bg_color'], 150) . "}";}if ($data['full_width'] == 'only_grid'){    echo "\n#page-container .row{margin-left:-5px;}";}if ($data['footer_bg_color']) {    echo "\n#footer {";    echo "background-color:" . $data['footer_bg_color'] . "}";}
